import React from "react";

const Layout = ({ children, narrow = true }) => {
  return (
    <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8 relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-600 via-blue-500 to-teal-500"></div>
      <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-20"></div>

      <div
        className={`relative w-full ${
          narrow ? "max-w-md" : "max-w-6xl"
        } mx-auto`}
      >
        <div className="bg-white/95 backdrop-blur-sm p-8 rounded-2xl shadow-2xl border border-white/20">
          {children}
        </div>
      </div>
    </div>
  );
};

export default Layout;
